import React, {useState, useEffect} from 'react'
import Board from '../board/Board'
import LetterTray from '../LetterTray/LetterTray'
import ResizeObserver from 'resize-observer-polyfill';
import './Game.css'
export default function Game() {
    const [isMouseDown, setIsMouseDown] = useState(false);
    const [mousePosition, setMousePosition] = useState({x: 0, y: 0, letter:''})
    const [gameData, setGameData] = useState({board:[[0,1,0, 0,1,0],[0,'e',0, 0,1,0],[0,0,0, 0,1,0]],tray:['a','c','b','c']})
    function getClosest(x, y) {
        const elements = document.querySelectorAll('[id*="play"]'); // Get elements with IDs containing "play"
        let closestElement = null;
        let minDistance = Infinity;

        elements.forEach((element) => {
          const rect = element.getBoundingClientRect();
          const centerX = rect.left + rect.width / 2;
          const centerY = rect.top + rect.height / 2;

          const distance = Math.sqrt((centerX - x) ** 2 + (centerY - y) ** 2);

          if (distance < minDistance) {
            minDistance = distance;
            closestElement = element;
          }
        });
        console.log(minDistance < 25)
        if(minDistance > 25){
            return "failure,failure"
        }

        return closestElement.id;
      }
    useEffect(()=>{
        console.log("IT WORKD: " , mousePosition)
        const id=getClosest(mousePosition.x, mousePosition.y).split(',')
        console.log(id)
        if(id[0] !== "failure"){
            const y = parseInt(id[0])
            const x = parseInt(id[1].split("_")[0])
            console.log(id,x,y)
            setGameData((prevData) =>{
                const newArray = [...prevData.board];
                newArray[y][x] = 'g';
                return { ...prevData, board: newArray };
            })
        }
    },[mousePosition])
  return (
    <> 
    <div className='board-container'>
        <Board data={gameData.board}></Board>
    </div>
    <div className='board-container'>
        <LetterTray isMouseDown={isMouseDown} setIsMouseDown={setIsMouseDown} setMousePosition={setMousePosition} letters={gameData.tray}></LetterTray>
    </div>
    </>
  )
}
