import React from 'react'
import './Board.css'
import { v4 as uuidv4 } from 'uuid';

export default function Board(props) {
    let board =[];
    let height = props.data.length
    //Sorting the grid into rocks, open spaces, and filled spaces
    for (let i=0; i<height; i++){
        board.push([])
        let width = props.data[i].length
        for (let j=0; j<width; j++){
            var letter = props.data[i][j]
            if(letter === 0){
                board[i].push(<div className='emptySquare' id={`${i}_${j}_play`}></div>)
            } else if ( letter === 1){
                board[i].push(<div className='rockSquare' id={`${i},${j}`}></div>)
            } else{
                board[i].push(<div className='letterSquare' id={`${i}_${j}_play`}>{letter}</div>)
            }
        }
    };

    return (
    <div>{board.map((row)=>(
        <div className='row' key={uuidv4()}>
        {row.map((space)=>(
            <div key={uuidv4()}>{space}</div>
        ))}
        </div>
    ))}</div>
  )
}
