import React from 'react'
import './Tile.css' 
export default function Tile(props) {
    var tiles = []
    var counter = 0;
    function topLeft(j, jLen, i, iLen){
       if( j>0 && props.tileArr[i][j-1] !=  0){
        return true
       }
       if( i>0){
        return true
       }
       return false
    }
    function topRight(j, jLen, i, iLen){
        if(j<jLen-1){
            return true;
        }
        if( i>0){
            return true
        }
        return false;

    }
    function botRight(j, jLen, i, iLen){
        if(j<jLen-1){
            return true;
        }
        if(i<iLen-1 && props.tileArr[i+1][j]!=  0){
            return true
        }
        return false;
    }
    function botLeft(j, jLen, i, iLen){
        if( j>0 && props.tileArr[i][j-1] !=  0){
            return true
           }
           return false
    }
    let iLen = props.tileArr.length
    for (let i=0; i<iLen; i++){
        tiles.push([])
        let jLen = props.tileArr[i].length
        for (let j=0; j<jLen; j++){
            var letter = props.tileArr[i][j]
            if(letter){
                console.log(j,jLen)
                var radius = `${topLeft(j,jLen, i, iLen) ? '0px' : "5px"} ${topRight(j,jLen, i, iLen) ? '0px' : "5px"} ${botRight(j,jLen, i, iLen) ? '0px' : "5px"} ${botLeft(j,jLen, i, iLen) ? '0px' : "5px"}`
                var borderBot = `${topLeft(j,jLen, i, iLen) ? '2px solid #b58a64' : "2px solid #876648"}`
                var borderTop = `${topLeft(j,jLen, i, iLen) ? '2px solid #b58a64' : "2px solid #876648"}`
                var borderLeft = `${j>0 && props.tileArr[i][j-1] !=  0 ? '2px solid #b58a64' : "2px solid #876648"}`
                var borderRight = `${j<jLen-1 ? '2px solid #b58a64' : "2px solid #876648"}`
                tiles[counter].push(<div className='singleTile' style={{borderRadius:radius, borderBottom:borderBot, borderTop:borderTop, borderLeft:borderLeft, borderRight:borderRight}}>{letter}</div>)
            } else {
                tiles[counter].push(<div className='empty'></div>)
            }
        }
        counter++;
    };
    console.log(tiles)

  return (
    <>
    <div>{tiles.map((row)=>(
        <div className='row'>
        {row.map((letter)=>(
            <div>{letter}</div>
        ))}
        </div>
    ))}</div>
    {/* <div className='wholePiece'>{tiles}</div> */}
    </>
  )
}
